function initializeNavigation(){const navLinks=document.querySelectorAll(".nav-link");const sections=document.querySelectorAll("section");const navToggle=document.querySelector(".nav-toggle");const navMenu=document.querySelector(".navbar-nav");navLinks.forEach(link=>{link.addEventListener("click",e=>{e.preventDefault();const targetId=link.getAttribute("href").substring(1);const targetSection=document.getElementById(targetId);if(targetSection){targetSection.scrollIntoView({behavior:"smooth"});if(navMenu.classList.contains("active")){navMenu.classList.remove("active");navToggle.classList.remove("active")}}})});if(navToggle&&navMenu){navToggle.addEventListener("click",()=>{navToggle.classList.toggle("active");navMenu.classList.toggle("active")});document.addEventListener("click",e=>{if(navMenu.classList.contains("active")&&!e.target.closest(".navbar-nav")&&!e.target.closest(".nav-toggle")){navMenu.classList.remove("active");navToggle.classList.remove("active")}})}}function initializeScrollEffects(){const header=document.querySelector("header");const scrollTop=document.querySelector(".scroll-top");const sections=document.querySelectorAll("section");const navLinks=document.querySelectorAll(".nav-link");window.addEventListener("scroll",()=>{if(window.scrollY>50){header.classList.add("scrolled");if(scrollTop)scrollTop.classList.add("active")}else{header.classList.remove("scrolled");if(scrollTop)scrollTop.classList.remove("active")}let current="";sections.forEach(section=>{const sectionTop=section.offsetTop;const sectionHeight=section.clientHeight;if(window.scrollY>=sectionTop-200){current=section.getAttribute("id")}});navLinks.forEach(link=>{link.classList.remove("active");if(link.getAttribute("href").substring(1)===current){link.classList.add("active")}})});if(scrollTop){scrollTop.addEventListener("click",e=>{e.preventDefault();window.scrollTo({top:0,behavior:"smooth"})})}}function initializeTypingEffect(){const typingElement=document.querySelector(".typing-text");if(!typingElement)return;const text=typingElement.textContent;typingElement.textContent="";let i=0;function typeWriter(){if(i<text.length){typingElement.textContent+=text.charAt(i);i++;setTimeout(typeWriter,100)}}typeWriter()}function initializeProjectFilters(){const filterButtons=document.querySelectorAll(".filter-btn");const projectCards=document.querySelectorAll(".project-card");filterButtons.forEach(button=>{button.addEventListener("click",()=>{filterButtons.forEach(btn=>btn.classList.remove("active"));button.classList.add("active");const filter=button.getAttribute("data-filter");projectCards.forEach(card=>{if(filter==="all"||card.getAttribute("data-category")===filter){card.style.display="block";setTimeout(()=>card.style.opacity="1",50)}else{card.style.opacity="0";setTimeout(()=>card.style.display="none",300)}})})})}function initializeContactForm(){const contactForm=document.getElementById("contact-form");if(!contactForm){return}emailjs.init("i-A0mSr_14xew95Lk");contactForm.addEventListener("submit",async e=>{e.preventDefault();const formData=new FormData(contactForm);const data=Object.fromEntries(formData.entries());if(!data.name||!data.email||!data.message){alert("Please fill in all fields");return}const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;if(!emailRegex.test(data.email)){alert("Please enter a valid email address");return}const submitBtn=contactForm.querySelector(".submit-btn");const originalBtnText=submitBtn.innerHTML;submitBtn.innerHTML='<i class="fas fa-spinner fa-spin"></i> Sending...';submitBtn.disabled=true;try{await emailjs.send("service_6zgn7ww","template_ziwkefk",{from_name:data.name,from_email:data.email,subject:data.subject,message:data.message,to_email:"sandiegoles8@gmail.com"});alert("Thank you for your message! I will get back to you soon.");contactForm.reset()}catch(error){console.error("EmailJS send error:",error);alert("Sorry, there was an error sending your message. Please try again later.")}finally{submitBtn.innerHTML=originalBtnText;submitBtn.disabled=false}})}function initializeCertificateModal(){const certImages=document.querySelectorAll(".certificate-img");const certModal=document.getElementById("certificate-modal");const modalImg=document.getElementById("modal-img");const modalClose=document.getElementById("modal-close");if(certImages.length>0&&certModal&&modalImg&&modalClose){certImages.forEach(img=>{img.addEventListener("click",function(){certModal.classList.add("active");modalImg.src=this.src;modalImg.alt=this.alt;modalImg.classList.remove("zoomed")});img.addEventListener("keypress",function(e){if(e.key==="Enter"||e.key===" "){certModal.classList.add("active");modalImg.src=this.src;modalImg.alt=this.alt;modalImg.classList.remove("zoomed")}})});modalImg.addEventListener("click",function(){this.classList.toggle("zoomed")});modalClose.addEventListener("click",function(){certModal.classList.remove("active");modalImg.src="";modalImg.classList.remove("zoomed")});certModal.addEventListener("click",function(e){if(e.target===certModal){certModal.classList.remove("active");modalImg.src="";modalImg.classList.remove("zoomed")}});document.addEventListener("keydown",function(e){if(certModal.classList.contains("active")){if(e.key==="Escape"){certModal.classList.remove("active");modalImg.src="";modalImg.classList.remove("zoomed")}}})}}const projectCards=document.querySelectorAll(".project-card");const observerOptions={threshold:.1,rootMargin:"0px 0px -50px 0px"};const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.opacity="1";entry.target.style.transform="translateY(0)"}})},observerOptions);projectCards.forEach(card=>{card.style.opacity="0";card.style.transform="translateY(20px)";card.style.transition="all 0.6s ease-out";observer.observe(card)});document.addEventListener("DOMContentLoaded",()=>{const parallaxElement=document.querySelector(".parallax-element");const shapes=document.querySelectorAll(".shape");document.addEventListener("mousemove",e=>{const mouseX=e.clientX/window.innerWidth;const mouseY=e.clientY/window.innerHeight;shapes.forEach((shape,index)=>{const speed=(index+1)*.2;const x=(mouseX-.5)*speed*100;const y=(mouseY-.5)*speed*100;const z=(mouseX-.5)*speed*50;shape.style.transform=`translate3d(${x}px, ${y}px, ${z}px)`});const moveX=(mouseX-.5)*20;const moveY=(mouseY-.5)*20;parallaxElement.style.transform=`translate3d(${moveX}px, ${moveY}px, 0)`});document.addEventListener("mouseleave",()=>{shapes.forEach(shape=>{shape.style.transform="translate3d(0, 0, 0)"});parallaxElement.style.transform="translate3d(0, 0, 0)"})});